mapper.select:
SELECT zaci.telphone, zai.account_id, zai.source, zai.status, zai.real_name, zai.idno, zai.updated_time, zai.company_full_name, zavl.created_time AS check_time, zsi.real_name AS check_by FROM zh_agent_info zai INNER JOIN zh_account_info zaci ON zai.account_id = zaci.account_id LEFT JOIN ( ( SELECT target_id, created_by, created_time FROM zh_account_verify_log WHERE target_type = #{targetType} AND pid IN ( SELECT max(pid) FROM zh_account_verify_log a WHERE target_type = #{targetType} GROUP BY target_id ORDER BY created_time DESC ) ) zavl INNER JOIN zh_staff_info zsi ON zavl.created_by = zsi.staff_id ) ON zai.account_id = zavl.target_id
zaci.telphone=#{telphone}
and zai.status=#{status}
and zai.company_full_name like #{companyName}
and zai.real_name like #{realName}
and zai.is_deleted=#{isDeleted}
and (zaci.telphone like #{queryContent} or zai.company_full_name like #{queryContent} or zai.real_name like #{queryContent})
order by FIELD(zai.status,'01','02','00'),zai.updated_time desc
