SELECT zqi.quoted_id, zsbi.source_no, zqi.status, zosi.source_id, DATE_FORMAT(zosi.need_arrive_time,'%Y-%m-%d %H:%i') as arrive_customer_time, DATE_FORMAT(zobi.need_arrive_time,'%Y-%m-%d %H:%i') as need_arrive_time, DATE_FORMAT(zobi.need_start_time,'%Y-%m-%d %H:%i') as need_start_time, zosi.need_driver_num, zosi.miles, zosi.start_code_p, zosi.start_code_c, zosi.estimated_price, zosi.transit_require, zosi.created_time, zosi.end_code_p, zosi.end_code_c, zosi.need_type, zosi.need_length, zosi.remark as source_remark, DATE_FORMAT(zosi.start_time,'%Y-%m-%d %H:%i') as start_time, DATE_FORMAT(zosi.end_time,'%Y-%m-%d %H:%i') as end_time, zodi.goods_volume, zodi.goods_weight, zobi.goods_name, zoli.transfer_code_p, zoli.transfer_code_c, ( SELECT count(1) FROM zh_quoted_info zqi WHERE zqi.source_id = zosi.source_id ) AS quote_num,
1 as has_quote, CASE WHEN now() >= zosi.start_time THEN TIMESTAMPDIFF(SECOND, now(), zosi.end_time) ELSE TIMESTAMPDIFF(SECOND, now(), zosi.start_time) END AS ago_time, now() >= zosi.start_time AS has_begin, zfs.NAME AS status_cn, zosis.area_code as sender_area_code, zosis.address as sender_address, zsai.addr_name as store_addr_name, zsai.province_code as store_province_code, zsai.city_code as store_city_code, zsai.district_code as store_district_code, zsai.address as store_address FROM zh_quoted_info zqi INNER JOIN zh_order_source_info zosi ON zqi.source_id = zosi.source_id INNER JOIN ( zh_source_base_info zsbi INNER JOIN ( zh_order_detail_info zodi INNER JOIN ( zh_order_base_info zobi INNER JOIN zh_order_line_info zoli ON zoli.info_id = zobi.info_id inner join zh_order_sender_info zosis on zosis.info_id = zobi.info_id left join zh_store_addr_info zsai on zsai.addr_id = zobi.addr_id ) ON zobi.info_id = zodi.info_id ) ON zsbi.order_id = zodi.order_id ) ON zosi.info_id = zsbi.info_id INNER JOIN ( zh_flow_status zfs INNER JOIN zh_flows zf ON zf.flow_id = zfs.flow_id AND zf.NAME = #{flowName} ) ON zfs.CODE = zqi.STATUS WHERE zqi.quoted_id in
#{quoteId}
