mapper.select:
select t1.feedback_id, t1.load_id, t1.type, t1.item_name, t1.item_price, t1.item_desc, date_format(t1.feedback_time,'%Y-%m-%d %H:%i') as feedback_time, t1.longitude, t1.latitude, t1.address, t1.status, t1.is_exception, t1.is_end, t1.speed, case when t1.is_end = '01' then date_format(t1.end_time,'%Y-%m-%d %H:%i') else "" end as end_time, date_format(t1.created_time,'%Y-%m-%d %H:%i') as created_time, t2.feedback_id as atta_feedback_id, t2.file_id as atta_file_id, case when t1.source = '01' then t3.real_name else t4.real_name end as real_name, case when t1.source = '01' then t3.position else "司机" end as position from zh_load_feedback_log t1 left join zh_load_feedback_atta t2 on t1.feedback_id=t2.feedback_id left join zh_staff_info t3 on t1.created_by = t3.staff_id left join zh_agent_info t4 on t1.created_by=t4.account_id
and t1.load_id=#{loadId}
and t1.is_exception=#{isException}
and t1.is_end=#{isEnd}
and t1.is_deleted=#{isDeleted}
and t1.source=#{source}
and t1.type in
#{type}
and t1.status in
#{status}
order by t1.feedback_time,t1.created_time

SELECT zlfl.feedback_id, zlfl.load_id, zlfl.type, zlfl.item_name, zlfl.item_price, zlfl.item_desc, date_format(zlfl.feedback_time,'%Y-%m-%d %H:%i') as feedback_time, zlfl.longitude, zlfl.latitude, zlfl.address, zlfl.speed, zlfl.status, zlfl.is_exception, zlfl.is_end, case when zlfl.is_end = '01' then date_format(zlfl.end_time,'%Y-%m-%d %H:%i') else "" end as end_time, CASE WHEN zlfl.source = #{cbsSource} THEN zsi.real_name ELSE zaia.real_name END AS real_name FROM zh_load_feedback_log zlfl LEFT JOIN zh_staff_info zsi ON zsi.staff_id = zlfl.created_by AND zlfl.source = #{cbsSource} LEFT JOIN (zh_account_info zai inner join zh_agent_info zaia on zai.account_id = zaia.account_id) ON zai.account_id = zlfl.created_by AND zlfl.source = #{ysjSource} WHERE zlfl.status = #{status} AND zlfl.load_id = #{loadId} and zlfl.is_deleted = #{isDeleted}
and zlfl.longitude is not null and zlfl.latitude is not null and zlfl.longitude <> '' and zlfl.latitude <> ''
ORDER BY zlfl.feedback_time

	select sum(item_price)   from zh_load_feedback_log



      and load_id=#{loadId}


      and type=#{type}


      and is_exception=#{isException}


      and is_deleted=#{isDeleted}

  	select count(1) from zh_load_feedback_log 
  	where 
	(item_price is null or item_price ="")  

        and load_id=#{loadId}


        and type=#{type}


        and is_exception=#{isException}


        and is_deleted=#{isDeleted}
