SELECT zodi.order_id AS orderId, zodi.order_no AS orderNo, zosi.customer_id AS customerId, cust.full_name AS customerName, zodi.created_time AS createdTime, zci.consign_id AS consignId, zci.consign_no AS consignNo, zci.created_time AS consignCreatedTime, zodi.status, zoli.load_id as loadId, zoli.load_no as loadNo, zoli.created_time as loadCreatedTime, zlti.trip_time as tripTime, zrvi.plate_number as plateNumber FROM zh_order_detail_info zodi INNER JOIN ( zh_order_base_info zobi LEFT JOIN ( zh_order_sender_info zosi LEFT JOIN zh_customer_info cust ON zosi.customer_id = cust.customer_id ) ON zobi.info_id = zosi.info_id ) ON zodi.info_id = zobi.info_id
AND zobi.type = #{type}
LEFT JOIN zh_consignment_info zci ON zci.order_id = zodi.order_id
and zci.is_deleted=#{isDeleted}
LEFT JOIN ( ( SELECT order_id, vehicle_info_id, load_no, load_id, created_time FROM zh_order_load_info
where status in
#{status}
and type = #{loadType} ) zoli LEFT JOIN ( SELECT vehicle_info_id, plate_number FROM zh_real_vehicle_info
where is_deleted=#{isDeleted}
) zrvi ON zoli.vehicle_info_id = zrvi.vehicle_info_id LEFT JOIN zh_load_trip_info zlti on zoli.load_id = zlti.load_id ) ON zoli.order_id = zodi.order_id
(zodi.order_no=#{keyWord} or zci.consign_no=#{keyWord} or zobi.customer_bill_no=#{keyWord} or zoli.load_no =#{keyWord} or zrvi.plate_number = #{keyWord} or cust.full_Name like #{keyWordLike})
and zodi.is_deleted=#{isDeleted}
order by zodi.created_time
asc
desc

SELECT
	de.order_id AS orderId,
	de.order_no AS orderNo,
	cust.full_name AS customerName,
	line.start_code_p AS startCodeP,
	line.start_code_c AS startCodeC,
	line.end_code_p AS endCodeP,
	line.end_code_c AS endCodeC,
	base.line_attribute AS lineAttribute,
	de.status,
	zorl.status as retStatus,
	de.created_time AS createdTime
FROM
	zh_order_detail_info de
INNER JOIN (
	zh_order_base_info base
	LEFT JOIN zh_order_receiver_info re ON base.info_id = re.info_id
	LEFT JOIN zh_order_line_info line ON base.info_id = line.info_id
	LEFT JOIN zh_department_info depart ON base.belong_depart_id = depart.depart_id
	LEFT JOIN (
		zh_order_sender_info se
		LEFT JOIN zh_customer_info cust ON se.customer_id = cust.customer_id
	) ON base.info_id = se.info_id
) ON de.info_id = base.info_id
LEFT JOIN zh_consignment_info zci ON zci.order_id = de.order_id	

        and zci.is_deleted=#{isDeleted}

LEFT JOIN (
	zh_source_base_info zsbi
	INNER JOIN (
		(
			SELECT
				*
			FROM
				zh_order_source_info
			WHERE
				created_time IN (
					SELECT
						max(created_time)
					FROM
						zh_order_source_info
					GROUP BY
						info_id
				)
		) AS zosi
		LEFT JOIN (select * from zh_order_return_log where pid in(select max(pid) from zh_order_return_log group by order_id)) as zorl ON zosi.source_id = zorl.source_id
	) ON zsbi.info_id = zosi.info_id
) ON de.order_id = zsbi.order_id


         (de.order_no=#{keyWord}
        or base.customer_bill_no=#{keyWord}
        or se.telphone like #{keyWordLike}
        or se.mobile = #{keyWord}
        or cust.full_Name like #{keyWordLike})


        and se.customer_id = #{customerId}


        and cust.full_name like #{customerName}


        and base.agreement_Id=#{agreementId}


        and de.created_time >= #{createdTimeS}


        and de.created_time <= #{createdTimeE}


        and depart.name like #{createDepartName}


        and line.start_code_p=#{lineStartP}


        and line.start_code_c=#{lineStartC}


        and line.end_code_p=#{lineEndP}


        and line.end_code_c=#{lineEndC}


        and se.contact_name like #{sendContractName}


        and se.mobile=#{sendMobile}


        and re.contact_name like #{reciContractName}


        and re.mobile=#{reciMobile}


        and de.is_deleted=#{isDeleted}


        and de.status in  

 		#{eachStatus}



        and zorl.status=#{retStatus}


	 	and de.status not in

	        	#{status}



  	 order by de.created_time

       	 asc


       	 desc

SELECT zorl.pid, zorl.order_id as orderId, zodi.order_no as orderNo, zosi.source_id as sourceId, zosi.start_code_p as startCodeP, zosi.start_code_c as startCodeC, zosi.end_code_p as endCodeP, zosi.end_code_c as endCodeC, zobi.need_start_time as needStartTime, zobi.need_arrive_time as needArriveTime, zobi.need_length as needLength, zobi.need_type as needType, zobi.remark as orderRemark, zosi.remark as sourRemark, zodi.status as orderStatus, zorl.status as retStatus, zosi.created_time as createdTime FROM zh_order_return_log zorl INNER JOIN ( zh_order_detail_info zodi INNER JOIN zh_order_base_info zobi ON zodi.info_id = zobi.info_id ) ON zorl.order_id = zodi.order_id INNER JOIN zh_order_source_info zosi ON zorl.source_id = zosi.source_id
and zodi.order_no=#{queryContent}
and zorl.status=#{status}
and zodi.is_deleted=#{isDeleted}

SELECT
	distinct
	zrvi.plate_number,
	zodi.order_id,
	zodi.order_no,
	line.start_code_p,
	line.start_code_c,
	line.end_code_p,
	line.end_code_c,
	zlti.trip_time,
	zoli.load_id,
	zobi.need_arrive_time	
FROM
	(select order_id,order_no,status,info_id from zh_order_detail_info where status = #{status}) zodi
INNER JOIN (
	zh_order_base_info zobi
	LEFT JOIN zh_order_sender_info zosi ON zobi.info_id = zosi.info_id
	LEFT JOIN zh_order_line_info line on zobi.info_id = line.info_id
) ON zodi.info_id = zobi.info_id 
INNER JOIN (
	zh_order_load_info zoli
	LEFT JOIN zh_real_vehicle_info zrvi ON zoli.vehicle_info_id = zrvi.vehicle_info_id 
	LEFT JOIN (
		zh_load_driver_info zldi
		INNER JOIN zh_real_driver_info zrdi ON zldi.driver_info_id = zrdi.driver_info_id 
	) ON zoli.load_id = zldi.load_id
	INNER JOIN zh_load_trip_info zlti on zoli.load_id = zlti.load_id
) ON zodi.order_id = zoli.order_id and zoli.type = #{loadType}


		(zodi.order_no = #{keyWord} or zrvi.plate_number = #{keyWord} or zoli.load_no = #{keyWord})


		and zosi.customer_id = #{customerId}


		and line.start_code_p = #{startCodeP}


		and line.start_code_c = #{startCodeC}


		and line.end_code_p = #{endCodeP}


		and line.end_code_c = #{endCodeC}


		and zlti.customer_bill_no = #{customerBillNo}



		and zlti.trip_time >= #{tripTimeStart}



		and zlti.trip_time <= #{tripTimeEnd}



		and zrdi.real_name like #{realName}


		and zrdi.telphone = #{telphone}



	order by zlti.created_time desc


	order by zlti.trip_time asc


	order by zlti.trip_time desc


	order by zobi.need_arrive_time asc


	order by zobi.need_arrive_time desc

SELECT zrvi.vehicle_info_id, zrvi.plate_number, zodi.order_id, zodi.order_no, zoli.load_id, zoli.load_no, zlti.customer_bill_no, zosi.customer_name, line.start_code_p, line.start_code_c, line.end_code_p, line.end_code_c, zrdi.driver_info_id, zrdi.real_name, zrdi.idno, zrdi.telphone, zldi.is_major, zrvi.type, zrvi.length, zobi.need_start_time, zobi.need_arrive_time, zlti.trip_time FROM (select order_id,order_no,status,info_id from zh_order_detail_info
order_id = #{orderId}
and status = #{status}
) zodi INNER JOIN ( zh_order_base_info zobi LEFT JOIN zh_order_sender_info zosi ON zobi.info_id = zosi.info_id LEFT JOIN zh_order_line_info line on zobi.info_id = line.info_id ) ON zodi.info_id = zobi.info_id INNER JOIN ( (select * from zh_order_load_info
load_id = #{loadId}
) zoli LEFT JOIN zh_real_vehicle_info zrvi ON zoli.vehicle_info_id = zrvi.vehicle_info_id LEFT JOIN ( zh_load_driver_info zldi INNER JOIN zh_real_driver_info zrdi ON zldi.driver_info_id = zrdi.driver_info_id ) ON zoli.load_id = zldi.load_id INNER JOIN zh_load_trip_info zlti on zoli.load_id = zlti.load_id ) ON zodi.order_id = zoli.order_id and zoli.type = #{loadType}

SELECT zodi.order_id, zodi.order_no, zoli.load_id, zci.consign_no, zci.consign_id, zosi.customer_name, line.start_code_p, line.start_code_c, line.end_code_p, line.end_code_c, zci.freight_price, zci.created_time as consign_created_time, zci.status FROM zh_order_detail_info zodi INNER JOIN ( zh_order_base_info zobi LEFT JOIN zh_order_line_info line ON zobi.info_id = line.info_id LEFT JOIN zh_order_sender_info zosi ON zobi.info_id = zosi.info_id ) ON zodi.info_id = zobi.info_id INNER JOIN ( ( SELECT consign_id, consign_no, order_id, freight_price, load_price, other_price, created_time, status FROM zh_consignment_info WHERE status IN
#{consignStatus}
) zci INNER JOIN zh_order_base_info zobin ON zci.consign_id = zobin.info_id AND zobin.type = #{type} ) ON zodi.order_id = zci.order_id INNER JOIN ( ( SELECT load_id, order_id FROM zh_order_load_info WHERE status IN
#{loadStatus}
) zoli INNER JOIN ( SELECT load_id, ( CASE WHEN source = '01' THEN op_time ELSE updated_time END ) AS elec_receipt_time, created_time, updated_time from zh_load_elecreceipt where status = #{status} ) zle ON zoli.load_id = zle.load_id ) ON zodi.order_id = zoli.order_id
(zodi.order_no = #{keyWord} or zci.consign_no = #{keyWord})
and zosi.customer_id = #{customerId}
and line.start_code_p = #{startCodeP}
and line.start_code_c = #{startCodeC}
and line.end_code_p = #{endCodeP}
and line.end_code_c = #{endCodeC}
and zobin.customer_bill_no = #{customerBillNo}
and zobin.is_predict = #{isPredict}
and zci.status = #{verifyStatus}
and zci.created_time >= #{consignStartTime}
and zci.created_time <= #{consignEndTime}
and zle.elec_receipt_time >= #{elecReceiptStartTime}
and zle.elec_receipt_time <= #{elecReceiptEndTime}
order by zle.updated_time desc
order by zci.created_time asc
order by zci.created_time desc

SELECT zodi.order_id, zodi.order_no, zoli.load_id, zci.status, zci.consign_id, zci.consign_no, zci.freight_price, zobi.customer_bill_no, zobi.payment_type, zosi.customer_name, line.start_code_p, line.start_code_c, line.end_code_p, line.end_code_c, zlti.trip_time, (case when zle.source ='01' then zle.op_time else zle.updated_time END ) as elec_receipt_time, (case when zlp.type = '01' then zlp.sign_time else zlp.updated_time END) as print_receipt_time FROM zh_order_detail_info zodi INNER JOIN ( zh_order_base_info zobi LEFT JOIN zh_order_line_info line ON zobi.info_id = line.info_id LEFT JOIN zh_order_sender_info zosi ON zosi.info_id = zobi.info_id ) ON zodi.info_id = zobi.info_id INNER JOIN zh_consignment_info zci ON zodi.order_id = zci.order_id AND zci.status in
#{consignStatus}
INNER JOIN ( zh_order_load_info zoli INNER JOIN zh_load_trip_info zlti ON zoli.load_id = zlti.load_id INNER JOIN (select * from zh_load_elecreceipt where status ='00') zle ON zoli.load_id = zle.load_id LEFT JOIN (select * from zh_load_printreceipt where status = '00') zlp ON zoli.load_id = zlp.load_id ) ON zodi.order_id = zoli.order_id where zodi.order_id = #{orderId}
