mapper.select:
select t1.quoted_id, t1.account_id, t1.source_id, t1.price, t1.remark, t1.status, t1.created_by, t1.created_time, t1.updated_by, t1.updated_time, t2.completed, t3.invalid, t5.real_name, t5.idno, t6.telphone, t7.hisRemark, t8.created_time as quoted_time from zh_quoted_info t1 left join (select count(1) completed,account_id from zh_quoted_info q1,zh_quoted_his q2 where q2.dest_status = '05' and q1.quoted_id = q2.quoted_id group by q1.account_id ) t2 on t2.account_id=t1.account_id left join (select count(1) invalid,q1.account_id from zh_quoted_info q1,zh_quoted_his q2 where q2.dest_status = '04' and q1.quoted_id = q2.quoted_id and q2.user_type= '03' group by q1.account_id ) t3 on t3.account_id=t1.account_id left join zh_agent_info t5 on t5.account_id = t1.account_id left join (select r1.content as hisRemark,r1.target_id from zh_remark_info r1 where r1.pid in ( select max(r2.pid) from zh_remark_info r2 where type = '01' group by r2.target_id ) ) t7 on t7.target_id = t1.quoted_id inner join zh_account_info t6 on t6. account_id = t1.account_id left join zh_quoted_his t8 on t1.quoted_id = t8.quoted_id and t8.dest_status = #{destStatus} where t1.is_deleted = '00' and t1.source_id=#{sourceId}
and t1.status in
#{quotedStatus}
order by FIELD(t1.status,'03','01'),t1.created_time desc

   select 
     quoted_id,
     account_id,
     source_id,
     price,
     remark,
     status,
     created_by,
     created_time,
     updated_by,
     updated_time,
     is_deleted
   from zh_quoted_info


        quoted_id=#{quotedId}


        and account_id=#{accountId}


        and source_id=#{sourceId}


        and price=#{price}


        and remark=#{remark}


        and status=#{status}


        and created_by=#{createdBy}


        and created_time=#{createdTime}


        and updated_by=#{updatedBy}


        and updated_time=#{updatedTime}


        and is_deleted=#{isDeleted}


        and status!=#{invalidStatus}


        and quoted_id!=#{bidQuotedId}


  		and status in 

	      #{quotedStatus}

   select 
     quoted_id,
     account_id,
     source_id,
     price,
     remark,
     status,
     created_by,
     created_time,
     updated_by,
     updated_time,
     is_deleted
   from zh_quoted_info


        quoted_id=#{quotedId}


        and account_id=#{accountId}


        and source_id=#{sourceId}


        and price=#{price}


        and remark=#{remark}


        and status=#{status}


        and created_by=#{createdBy}


        and created_time=#{createdTime}


        and updated_by=#{updatedBy}


        and updated_time=#{updatedTime}


        and is_deleted=#{isDeleted}


  		and status in 

	      #{quotedStatus}

select t1.quoted_id, t1.account_id, t1.source_id, t1.price, t1.remark, t1.status, t1.created_by, t1.created_time, t1.updated_by, t1.updated_time, t1.is_deleted from zh_quoted_info t1,zh_order_source_info t2 where t1.source_id = t2.source_id
and t1.account_id=#{accountId}
and t1.source_id=#{sourceId}
and t1.price=#{price}
and t1.remark=#{remark}
and t1.status=#{status}
and t1.is_deleted=#{isDeleted}
and t2.start_time <= now()
and t1.status in
#{quotedStatus}
order by t1.updated_time desc

select count(1) as count FROM zh_quoted_info where account_id = #{accountId}
and status in
#{status}
