mapper.select:
select 
  account_id,
  telphone,
  password,
  nick_name,
  user_head,
  account_source,
  created_time,
  updated_time,
  status,
  is_deleted
from zh_account_info


     account_id=#{accountId}


     and telphone=#{telphone}


     and password=#{password}


     and nick_name=#{nickName}


     and user_head=#{userHead}


     and account_source=#{accountSource}


     and created_time=#{createdTime}


     and updated_time=#{updatedTime}


     and status=#{status}


     and is_deleted=#{isDeleted}

select 
  account_id,
  telphone,
  password,
  nick_name,
  user_head,
  account_source,
  created_time,
  updated_time,
  status,
  is_deleted
from zh_account_info


     account_id=#{accountId}


     and telphone=#{telphone}


     and password=#{password}


     and nick_name=#{nickName}


     and user_head=#{userHead}


     and account_source=#{accountSource}


     and created_time=#{createdTime}


     and updated_time=#{updatedTime}


     and status=#{status}


     and is_deleted=#{isDeleted}

select zai.account_id, zai.nick_name, zai.user_head, zai.telphone, zai.password, zai.account_source, zai.created_time, zai.updated_time, zai.status, zai.is_deleted, zid.is_staff from zh_account_info zai left join zh_internal_driver zid on zai.account_id = zid.account_id
zai.account_id=#{accountId}
and zai.telphone = #{telphone}
and zai.status=#{status}
and zai.is_deleted=#{isDel}

select zai.account_id, zai.user_head, zai.telphone, zai.status, zai.created_time, zagi.real_name, zagi.idno, zagi.company_full_name, ( select created_time from zh_account_operation_log where account_id = zai.account_id order by created_time DESC limit 1 ) as operation_time, (select login_time from zh_login_history where account_id=zai.account_id order by login_time desc limit 1) as last_login_time, (SELECT zsi.real_name FROM zh_account_operation_log zaol inner join zh_staff_info zsi on zaol.created_by=zsi.staff_id WHERE zaol.account_id = zai.account_id ORDER BY zaol.created_time DESC LIMIT 1) as check_by from zh_account_info zai left join zh_agent_info zagi on zai.account_id=zagi.account_id
zai.is_deleted=#{isDeleted}
and zai.telphone=#{telphone}
and zagi.real_name like #{realName}
and zai.status=#{status}
and (zai.telphone like #{queryContent} or zagi.real_name like #{queryContent})
order by zai.status desc,operation_time desc

select t4.account_id from zh_order_load_info t1 inner join zh_load_driver_info t2 on t1.load_id = t2.load_id and t2.is_major=#{isMajor} inner join zh_real_driver_info t3 on t3.driver_info_id = t2.driver_info_id inner join zh_agent_info t4 on t4.idno = t3.idno where t1.load_id=#{loadId}
and t1.status in
#{status}
