mapper.select:
   select 
     zci.consign_id,
     zci.order_id,
     zci.consign_no,
     zci.freight_price,
     zci.load_price,
     zci.other_price,
     zci.other_explain,
     zci.total,
     zci.remark,
     zci.status,
     zci.created_by,
     zci.created_time,
     zci.updated_by,
     zci.updated_time,
     zci.is_deleted,
	 zobi.customer_bill_no
   from zh_consignment_info zci
	inner join (zh_order_detail_info zodi
	inner join zh_order_base_info zobi
	on zodi.info_id = zobi.info_id
	)
	on zodi.order_id = zci.order_id


        zci.consign_id=#{consignId}


        and zci.order_id=#{orderId}


        and zci.consign_no=#{consignNo}


        and zci.freight_price=#{freightPrice}


        and zci.load_price=#{loadPrice}


        and zci.other_price=#{otherPrice}


        and zci.other_explain=#{otherExplain}


        and zci.total=#{total}


        and zci.remark=#{remark}


        and zci.status=#{status}


        and zci.created_by=#{createdBy}


        and zci.created_time=#{createdTime}


        and zci.updated_by=#{updatedBy}


        and zci.updated_time=#{updatedTime}


        and zci.is_deleted=#{isDeleted}

select 
  consign_id,
  order_id,
  consign_no,
  freight_price,
  load_price,
  other_price,
  other_explain,
  total,
  remark,
  status,
  created_by,
  created_time,
  updated_by,
  updated_time,
  is_deleted
from zh_consignment_info


     consign_id=#{consignId}


     and order_id=#{orderId}


     and consign_no=#{consignNo}


     and freight_price=#{freightPrice}


     and load_price=#{loadPrice}


     and other_price=#{otherPrice}


     and other_explain=#{otherExplain}


     and total=#{total}


     and remark=#{remark}


     and status=#{status}


     and created_by=#{createdBy}


     and created_time=#{createdTime}


     and updated_by=#{updatedBy}


     and updated_time=#{updatedTime}


     and is_deleted=#{isDeleted}
