mapper.insert:
insert into zh_load_driver_info (
  load_id,
  driver_info_id,
  is_major
)values (
  #{loadId},
  #{driverInfoId},
  #{isMajor}
)

mapper.update:
update zh_load_driver_info set
  load_id=#{loadId},
  driver_info_id=#{driverInfoId},
  is_major=#{isMajor}
where 
 driver_info_id=#{driverInfoId} 
 and load_id=#{loadId}

   update zh_load_driver_info


	       load_id=#{loadId},


	       driver_info_id=#{driverInfoId},


	       is_major=#{isMajor}



   where 
    driver_info_id=#{driverInfoId} 
    and load_id=#{loadId}

mapper.delete:
delete from 
  zh_load_driver_info 
where 
 driver_info_id=#{driverInfoId} 
 and load_id=#{loadId}

mapper.select:
select zldi.load_id, zldi.driver_info_id, zldi.is_major, zrdi.telphone, zrdi.real_name, zrdi.idno from zh_load_driver_info zldi inner join zh_real_driver_info zrdi on zldi.driver_info_id = zrdi.driver_info_id and zrdi.is_deleted = #{isDeleted}
zldi.load_id=#{loadId}
and driver_info_id=#{driverInfoId}
and is_major=#{isMajor}
order by zldi.is_major desc

select 
  load_id,
  driver_info_id,
  is_major
from zh_load_driver_info


     load_id=#{loadId}


     and driver_info_id=#{driverInfoId}


     and is_major=#{isMajor}
