mapper.delete:
delete from 
  zh_load_elecreceipt 
where 
 pid=#{pid} 
 and receipt_id=#{receiptId}

mapper.select:
select 
  pid,
  receipt_id,
  load_id,
  op_time,
  receipt_image_ref_id,
  status,
  source,
  created_by,
  created_time,
  updated_by,
  updated_time,
  is_deleted
from zh_load_elecreceipt


     pid=#{pid}


     and receipt_id=#{receiptId}


     and load_id=#{loadId}


     and op_time=#{opTime}


     and receipt_image_ref_id=#{receiptImageRefId}


     and status=#{status}


     and source=#{source}


     and created_by=#{createdBy}


     and created_time=#{createdTime}


     and updated_by=#{updatedBy}


     and updated_time=#{updatedTime}


     and is_deleted=#{isDeleted}

select 
  pid,
  receipt_id,
  load_id,
  op_time,
  receipt_image_ref_id,
  status,
  source,
  created_by,
  created_time,
  updated_by,
  updated_time,
  is_deleted
from zh_load_elecreceipt


     pid=#{pid}


     and receipt_id=#{receiptId}


     and load_id=#{loadId}


     and op_time=#{opTime}


     and receipt_image_ref_id=#{receiptImageRefId}


     and status=#{status}


     and source=#{source}


     and created_by=#{createdBy}


     and created_time=#{createdTime}


     and updated_by=#{updatedBy}


     and updated_time=#{updatedTime}


     and is_deleted=#{isDeleted}
