mapper.update:
update zh_load_exception
load_id=#{loadId},
reason=#{reason},
created_by=#{createdBy},
created_time=#{createdTime}
where load_id=#{loadId}

mapper.select:
select 
  load_id,
  reason,
  created_by,
  created_time
from zh_load_exception


     load_id=#{loadId}


     and reason=#{reason}


     and created_by=#{createdBy}


     and created_time=#{createdTime}

select 
  load_id,
  reason,
  created_by,
  created_time
from zh_load_exception


     load_id=#{loadId}


     and reason=#{reason}


     and created_by=#{createdBy}


     and created_time=#{createdTime}
