mapper.select:
select order_id, order_no, info_id, goods_weight, goods_volume, status, created_by, created_time, updated_by, updated_time, is_deleted from zh_order_detail_info
order_id=#{orderId}
and order_no=#{orderNo}
and is_deleted = '00'

select 
  order_id,
  order_no,
  info_id,
  goods_weight,
  goods_volume,
  status,
  created_by,
  created_time,
  updated_by,
  updated_time,
  is_deleted
from zh_order_detail_info


     order_id=#{orderId}


     and order_no=#{orderNo}


     and info_id=#{infoId}


     and goods_weight=#{goodsWeight}


     and goods_volume=#{goodsVolume}


     and status=#{status}


     and created_by=#{createdBy}


     and created_time=#{createdTime}


     and updated_by=#{updatedBy}


     and updated_time=#{updatedTime}


     and is_deleted=#{isDeleted}

