mapper.select:
select 
  info_id,
  customer_name,
  contact_name,
  telphone,
  mobile,
  area_code,
  address,
  longitude,
  latitude
from zh_order_receiver_info


     info_id=#{infoId}


     and customer_name=#{customerName}


     and contact_name=#{contactName}


     and telphone=#{telphone}


     and mobile=#{mobile}


     and area_code=#{areaCode}


     and address=#{address}


     and longitude=#{longitude}


     and latitude=#{latitude}

