mapper.select:
select 
  contract_id,
  freight_price,
  pre_pay,
  oil_pay,
  oil_card_no,
  exception_total,
  total,
  created_by,
  created_time
from zh_payables_info


     contract_id=#{contractId}


     and freight_price=#{freightPrice}


     and pre_pay=#{prePay}


     and oil_pay=#{oilPay}


     and oil_card_no=#{oilCardNo}


     and exception_total=#{exceptionTotal}


     and total=#{total}


     and created_by=#{createdBy}


     and created_time=#{createdTime}

select 
  contract_id,
